我认为最重要的就是将现实生活中的事物以及其部件抽象为计算机能够理解的一个类以及其下属的方法/属性等等。下面是一个用C#将人的头部（包括大脑、眼睛和嘴）抽象为一个类的例子：    class Program

    {

        static void Main(string[] args)

        {

            Head.Brain NewBrain=new Head.Brain();

            Head.Eye NewEye=new Head.Eye();

            Head.Mouth NewMouth=new Head.Mouth();

            string Input;

            Console.WriteLine(&quot;What Do I See?&quot;);

            Input = Console.ReadLine();

            Console.WriteLine(NewMouth.Eat(NewBrain.Think(NewEye.See(Input))));

        }

    }

        class Head

        {

            public class Brain

            {

                //大脑

                public string Think(string Content)

                {

                    //用大脑处理获得的信息

                    if (Content==&quot;I See Vegetable&quot;)//如果获得的信息是&quot;我看到了蔬菜&quot;

                    {

                        return &quot;Eat&quot;;//返回&quot;吃&quot;

                    }

                    else//否则

                    {

                        return &quot;Don't Eat&quot;;//返回&quot;不要吃&quot;

                    }

                }

            }

            public class Mouth

            {

                //嘴

                public string Eat(string Command)//嘴执行大脑的命令

                {

                    if (Command==&quot;Eat&quot;)//如果大脑的命令是&quot;吃&quot;

                    {

                        return &quot;Ate&quot;;//吃下蔬菜

                    }

                    else//否则

                    {

                        return &quot;Nothing Done&quot;;//什么也不干

                    }

                }

            }

            public class Eye

            {

                //眼睛

                public string See(string Image)

                {

                    //用眼睛观察眼前的事物

                    if (Image==&quot;Vegetable&quot;)//如果看到是蔬菜

                    {

                        return (&quot;I See Vegetable&quot;);//返回&quot;我看到了蔬菜&quot;

                    }

                    else//否则

                    {

                        return (&quot;I Didn't See Vegetable&quot;);//返回&quot;我没有看到蔬菜&quot;

                    }

                }

            }

    }
